import React from "react";
import { Container, Row, Col, Input, Button, Form } from "reactstrap";
import { useForm, Controller } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";

const signinSchema = yup.object().shape({
  identifier: yup.string().required("*Username is Required"),
  password: yup.string().required("*Password is Required"),
});

const AuthForm = () => {
  const { control, register, handleSubmit, errors } = useForm({
    resolver: yupResolver(signinSchema),
  });
  return (
    <Container fluid>
      <Row>
        <Col md={6} className="signin-box">
          <Row>
            <h4 className="signin-title">DEALER</h4>
          </Row>
          <Form onSubmit={handleSubmit(onsubmit)} className="login-form">
            <FormGroup>
              <Controller
                as={Input}
                type="text"
                name="identifier"
                defaultValue=""
                placeholder="username"
                control={control}
                refs={register}
              />
            </FormGroup>
            <FormGroup>
              <Controller
                as={Input}
                type="password"
                name="password"
                defaultValue=""
                placeholder="password"
                control={control}
                refs={register}
              />
            </FormGroup>
            <FormGroup>
              <Button className="signin-button">SIGN IN</Button>
            </FormGroup>
          </Form>
          <Row>
            <div className="or-div">OR</div>
          </Row>
          <Row>
            <a className="registernow-link">REGISTER NOW</a>
          </Row>
          <Row>
            <a className="forgotpassword-link">Forgot Password?</a>
          </Row>
        </Col>
        <Col md={6} className="register-box">
          <Row>
            <h4 className="signin-title">BUYER</h4>
          </Row>
          <Form onSubmit={handleSubmit(onsubmit)} className="login-form">
            <FormGroup>
              <Controller
                as={Input}
                type="text"
                name="identifier"
                defaultValue=""
                placeholder="username"
                control={control}
                refs={register}
              />
            </FormGroup>
            <FormGroup>
              <Controller
                as={Input}
                type="email"
                name="email"
                defaultValue=""
                placeholder="E-mail"
                control={control}
                refs={register}
              />
            </FormGroup>
            <FormGroup>
              <Controller
                as={Input}
                type="password"
                name="password"
                defaultValue=""
                placeholder="password"
                control={control}
                refs={register}
              />
            </FormGroup>
            <FormGroup>
              <Button className="signin-button">REGISTER</Button>
            </FormGroup>
          </Form>
        </Col>
      </Row>
    </Container>
  );
};

export default AuthForm;
